import { useState } from 'react';
import styles from './styles.module.css';
import Axios from 'axios';
import { Header, Userlist } from 'components';

export const App = () => {
  const [messageList, setMessageList] = useState([]);
  const [userList, setUserList] = useState([]);

  const getMessages = user => {
    Axios.get('http://localhost:3001/messages', {
      params: {
        user: user,
      },
    }).then(
      response => setMessageList(response.data),
      console.log(messageList),
    );
  };

  const getUsers = () => {
    Axios.get('http://localhost:3001/users').then(
      response => setUserList(response.data),
      console.log(messageList),
    );
  };

  return (
    <div className={styles.main}>
      <div className={styles.text}>
        <Header users={userList} />
        <Userlist />
        <div>
          <button onClick={getUsers}>Show Users</button>
          {userList.map((val, key) => {
            return (
              <div>
                <button onClick={() => getMessages(val.username)}>
                  {val.username}{' '}
                </button>
              </div>
            );
          })}
        </div>
        <div>
          {messageList.length
            ? messageList.map((val, key) => {
                return <div> {val.message} </div>;
              })
            : null}
        </div>
      </div>
    </div>
  );
};

export default App;
