import { Axios } from 'axios';
import React, { useEffect, useState } from 'react';

import { useSelector } from 'react-redux';
import { useLocation } from 'react-router';

import { TagCloud } from 'react-tagcloud';

export const WordCloud = user => {
  const API_GET_MSG = 'http://localhost:3001/messages';
  const [data, setData] = useState([]);
  const location = useLocation();
  let currentMessages;

  const getMessages = async () => {
    try {
      const response = await Axios.get(API_GET_MSG, {
        params: {
          user: user,
          id: location.id,
        },
      });
      console.log('GetMSGList Async', response);
      if (response.status === 200) {
        console.log('async GETUSER success');
        currentMessages = [response.data];
        return true;
      }
      return false;
    } catch (err) {
      console.log(err);
      throw new Error(err);
    }
  };

  const hash = {};
  console.log('curmsg prop', currentMessages);
  useEffect(() => {
    getMessages();
    setData([]);
    for (let i = 0; i < currentMessages.length; i++) {
      currentMessages[i].message.split(' ').forEach(el => {
        hash[el] = (hash[el] || 0) + 1;
      });
    }
    let counter = 0;
    for (const prop in hash) {
      if (hash[prop] > 3 && prop.length > 1) {
        let newObj = {
          value: prop,
          count: hash[prop],
          key: String(counter),
        };
        setData(oldArr => [...oldArr, newObj]);
        counter++;
        console.log('setstate data', data);
        console.log(user);
      }
    }
  }, [user]);

  const SimpleCloud = () => {
    if (data.length > 0) {
      return (
        <div>
          <TagCloud
            minSize={20}
            maxSize={55}
            tags={data}
            shuffle={false}
          />
        </div>
      );
    } else {
      return (
        <div>
          <h2> Not enough data to render wordcloud </h2>
        </div>
      );
    }
  };

  return (
    <div>
      {' '}
      <TagCloud minSize={20} maxSize={55} tags={data} shuffle={false} />
    </div>
  );
};

export const WordCloudMemo = React.memo(WordCloud);
