import styles from './styles.module.css';
import React, { useEffect, useState, useRef } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  getMessageList,
  liveMessageUpdateAsync,
  getMessageListAsync,
} from 'features/messagesSlice';
import { io } from 'socket.io-client';
import { current } from 'immer';
import { store } from 'store';

export const Messages = () => {
  let socket;

  let location = useLocation();
  let currentUser = location.state.name;
  let id = location.state.id;

  const dispatch = useDispatch();
  let currentMessages = useSelector(state => state.messages.messageList);
  console.log('MESSAGESJS', currentMessages);
  console.log(currentUser);

  if (currentMessages.length > 0) {
    console.log('IF COND', currentMessages.length);
  }

  const messagesEndRef = useRef(null);
  const scrollToBottom = () => {
    messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
  };
  useEffect(scrollToBottom, [currentMessages]);

