import React, { useEffect, useState } from 'react';

import { useSelector } from 'react-redux';

import { TagCloud } from 'react-tagcloud';

export const WordCloud = user => {
  let currentMessages = useSelector(state => state.messages.messageList);

  const [data, setData] = useState([]);

  const hash = {};

  useEffect(() => {
    setData([]);
    for (let i = 0; i < currentMessages.length; i++) {
      currentMessages[i].message.split(' ').forEach(el => {
        hash[el] = (hash[el] || 0) + 1;
      });
    }
    let counter = 0;
    for (const prop in hash) {
      if (hash[prop] > 30 && prop.length > 1) {
        let newObj = {
          value: prop,
          count: hash[prop],
          key: String(counter),
        };
        setData(oldArr => [...oldArr, newObj]);
        counter++;
        console.log('setstate data', data);
      }
    }
  }, [user]);

  const SimpleCloud = () => {
    if (data.length > 0) {
      return (
        <div>
          <TagCloud
            minSize={20}
            maxSize={55}
            tags={data}
            shuffle={false}
          />
        </div>
      );
    } else {
      return (
        <div>
          <h2> Loading Wordcloud... </h2>
        </div>
      );
    }
  };

  return (
    <div>
      {' '}
      <SimpleCloud />
    </div>
  );
};
