import styles from './styles.module.css';
import React, { useEffect, useState, useRef } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  getMessageList,
  liveMessageUpdateAsync,
  getMessageListAsync,
} from 'features/messagesSlice';
import { io } from 'socket.io-client';
import { current } from 'immer';
import { store } from 'store';
import { WordCloudMemo } from 'components';
import { TagCloud } from 'react-tagcloud';
import classNames from 'classnames';

export const Sentiment = () => {
  let currentMessages = useSelector(state => state.messages.messageList);
  let totalMessages = currentMessages.length;
  let posSentiment = 0;
  let negSentiment = 0;

  console.log('SENTIMENT', currentMessages);
  useEffect(() => {
    currentMessages.forEach(el => {
      if (el.sentiment > 0) posSentiment++;
      else if (el.sentiment < 0) negSentiment++;
    });
  });
  return (
    <div className={styles.main}>
      <h2> You have tracked {currentMessages.length} </h2>
      <p> Positive Sentiment - {posSentiment}</p>
      <p> Negative Sentiment - {negSentiment}</p>
      <p> {totalMessages} </p>
    </div>
  );
};
