import React, { useEffect } from 'react';

import { useSelector } from 'react-redux';

import { TagCloud } from 'react-tagcloud';

export const WordCloud = () => {
  let currentMessages = useSelector(state => state.messages.messageList);

  const hash = {};
  let data2 = [];

  useEffect(() => {
    for (let i = 0; i < currentMessages.length; i++) {
      currentMessages[i].message.split(' ').forEach(el => {
        hash[el] = (hash[el] || 0) + 1;
      });
    }
    let counter = 0;
    for (const prop in hash) {
      if (hash[prop] > 25) {
        data2.push({
          value: prop,
          count: hash[prop],
          key: String(counter),
        });
        counter++;
      }
    }
  }, []);

  const SimpleCloud = data => {
    if (data.length > 0) {
      return (
        <div>
          <TagCloud minSize={12} maxSize={35} tags={data2} />
        </div>
      );
    } else {
      return (
        <div>
          <h1> Loading Wordcloud </h1>
        </div>
      );
    }
  };

  return (
    <div>
      {' '}
      <SimpleCloud data={data2} />
    </div>
  );
};
