import styles from './styles.module.css';
import React, { useEffect, useState, useRef } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  getMessageList,
  liveMessageUpdateAsync,
  getMessageListAsync,
} from 'features/messagesSlice';
import { io } from 'socket.io-client';
import { current } from 'immer';
import { store } from 'store';
import { WordCloudMemo } from 'components';
import { TagCloud } from 'react-tagcloud';
import classNames from 'classnames';

export const Sentiment = messages => {
  let totalMessages = messages.length;

  const [overallSentiment, setOverallSentiment] = useState(0);

  useEffect(() => {
    console.log('SENTIMENT', messages.messages);
    messages.messages.forEach(el => {
      console.log('Sentiment analysis', el.sentiment);
      setOverallSentiment(prev => (prev += el.sentiment));
      console.log(overallSentiment);
    });
  }, []);
  return (
    <div className={styles.main}>
      <h2> You have tracked {messages.messages.length} </h2>
      <p> {overallSentiment} </p>
      <p> {totalMessages} </p>
    </div>
  );
};
