import styles from './styles.module.css';
import React, { useState } from 'react';
import Axios from 'axios';
import { useUsers, useAddUser } from 'hooks';
import { useSelector, useDispatch } from 'react-redux';
import { getUserList, showUserList } from 'features/usersSlice';

export const Header = users => {
  let currentUsers = useSelector();

  const [typedUser, setTypedUser] = useState('');

  const addUser = () => {
    let currentUser = typedUser;

    currentUser.length
      ? Axios.post('http://localhost:3001/create', {
          userName: currentUser,
        }).then(() => console.log('success'))
      : console.log('Im not ready yet');
  };

  return (
    <div className={styles.main}>
      <h1>
        {' '}
        You are currently tracking{' '}
        {currentUsers.length > 0 ? currentUsers.length : 0} users
      </h1>
      <div className={styles.text}>
        <h2>
          {' '}
          Please enter the Twitch account you'd like to track below{' '}
        </h2>
        <div className={styles.form}>
          <label>Twitch Account Name:</label>
          <input
            type="text"
            onChange={event => {
              setTypedUser(event.target.value);
            }}
          />
          <button onClick={addUser}> Track User</button>
        </div>
      </div>
    </div>
  );
};
