import styles from './styles.module.css';
import React, { useEffect, useState, useRef } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  getMessageList,
  liveMessageUpdateAsync,
  getMessageListAsync,
} from 'features/messagesSlice';
import { io } from 'socket.io-client';
import { current } from 'immer';
import { store } from 'store';
import { WordCloud } from 'components';
import { TagCloud } from 'react-tagcloud';

export const Messages = () => {
  let socket;

  let location = useLocation();
  let currentUser = location.state.name;
  let id = location.state.id;

  const dispatch = useDispatch();
  let currentMessages = useSelector(state => state.messages.messageList);
  console.log('MESSAGESJS', currentMessages);
  console.log(currentUser);

  const messagesEndRef = useRef(null);
  const scrollToBottom = () => {
    messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
  };
  useEffect(scrollToBottom, [currentMessages]);

  useEffect(() => {
    socket = io('http://localhost:3001', {
      transports: ['websocket'],
    });
    dispatch(getMessageListAsync(currentUser, id));
  }, []);

  useEffect(() => {
    socket.on(`${currentUser}`, data => {
      console.log('I received a notice from teh server', data);
      dispatch(liveMessageUpdateAsync(currentUser, id));
    });
  }, [socket]);

  return (
    <div className={styles.main}>
      <div>
        {data.length ? (
          <TagCloud minSize={5} maxSize={10} tags={data} />
        ) : (
          <></>
        )}
      </div>

      <div>
        {' '}
        <WordCloud />{' '}
      </div>
      <h1>
        {' '}
        You have tracked {currentMessages.length} messages in {currentUser}
        's chat
      </h1>
      <div className={styles.chatmessages}>
        {currentMessages.map((val, key) => {
          return (
            <div key={val.messages_id}>
              <hr />
              <p className={styles.text}> {val.message} </p>
            </div>
          );
        })}
        <div ref={messagesEndRef} />
      </div>
    </div>
  );
};
