import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { useSelector } from 'react-redux';

import { TagCloud } from 'react-tagcloud';

export const WordCloud = (user, id) => {
  const [data, setData] = useState([]);

  const hash = {};
  console.log('id', id, 'user', user);

  useEffect(() => {
    (async () => {
      try {
        const response = await Axios.get(
          'http://localhost:3001/messages',
          {
            params: {
              user: user,
              id: id,
            },
          },
        );
        console.log('useEffect async wC', response);
        if (response.status === 200) {
          setData([]);
          console.log(user, id);
          let currentMessages = response.data;

          for (let i = 0; i < currentMessages.length; i++) {
            currentMessages[i].message.split(' ').forEach(el => {
              hash[el] = (hash[el] || 0) + 1;
            });
          }
          let counter = 0;
          for (const prop in hash) {
            if (hash[prop] > 3 && prop.length > 1) {
              let newObj = {
                value: prop,
                count: hash[prop],
                key: String(counter),
              };
              setData(oldArr => [...oldArr, newObj]);
              counter++;
              console.log('setstate data', data);
              console.log(user);
            }
          }
          return true;
        }
        return false;
      } catch (err) {
        console.log(err);
        throw new Error(err);
      }
    })();
  }, [user]);
  console.log(data);
  const SimpleCloud = () => {
    if (data.length > 0) {
      return (
        <div>
          <TagCloud
            minSize={20}
            maxSize={55}
            tags={data}
            shuffle={false}
          />
        </div>
      );
    } else {
      return (
        <div>
          <h2> Not enough data to render wordcloud </h2>
        </div>
      );
    }
  };

  return (
    <div>
      {' '}
      <TagCloud minSize={20} maxSize={55} tags={data} shuffle={false} />
    </div>
  );
};

export const WordCloudMemo = React.memo(WordCloud);
