import styles from './styles.module.css';
import React, { useEffect, useState, useRef } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  getMessageList,
  liveMessageUpdateAsync,
  getMessageListAsync,
} from 'features/messagesSlice';
import { io } from 'socket.io-client';
import { current } from 'immer';
import { store } from 'store';
import { TagCloud } from 'react-tagcloud';

export const WordCloud = () => {
  let currentMessages = useSelector(state => state.messages.messageList);

  const hash = {};
  let data2 = [];

  useEffect(() => {
    for (let i = 0; i < currentMessages.length; i++) {
      currentMessages[i].message.split(' ').forEach(el => {
        hash[el] = (hash[el] || 0) + 1;
      });
    }
    let counter = 0;
    for (const prop in hash) {
      if (hash[prop] > 25) {
        data2.push({
          value: prop,
          count: hash[prop],
          key: String(counter),
        });
        counter++;
      }
    }
  }, []);

  const SimpleCloud = data => {
    console.log('simpcloud', data2);
    return (
      <div>
        <TagCloud minSize={12} maxSize={35} tags={data2} />
      </div>
    );
  };

  return <div>{data2.length > 0 ? <SimpleCloud /> : ''}</div>;
};
