import styles from './styles.module.css';
import React, { useEffect, useState, useRef } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  getMessageList,
  liveMessageUpdateAsync,
  getMessageListAsync,
} from 'features/messagesSlice';
import { io } from 'socket.io-client';
import { current } from 'immer';
import { store } from 'store';
import { TagCloud } from 'react-tagcloud';

export const WordCloud = () => {
  let currentMessages = useSelector(state => state.messages.messageList);

  const hash = {};
  let data = [];
  useEffect(() => {
    setTimeout(() => {
      for (let i = 0; i < currentMessages.length; i++) {
        currentMessages[i].message.split(' ').forEach(el => {
          hash[el] = (hash[el] || 0) + 1;
        });
      }
      let counter = 0;
      for (const prop in hash) {
        if (hash[prop] > 25) {
          data.push({
            value: prop,
            count: hash[prop],
            key: String(counter),
          });
          counter++;
        }
      }
      console.log(data);
    }, 1000);
  });
  console.log('TAGCLOUD', hash, data, currentMessages);
  // data = [
  //   {
  //     value: 'a',
  //     count: 35,
  //     key: '0',
  //   },
  //   {
  //     value: 'you',
  //     count: 27,
  //     key: '1',
  //   },
  //   {
  //     value: 'I',
  //     count: 27,
  //     key: '2',
  //   },
  //   {
  //     value: 'to',
  //     count: 30,
  //     key: '3',
  //   },
  //   {
  //     value: 'the',
  //     count: 32,
  //     key: '4',
  //   },
  // ];

  const SimpleCloud = mydata => {
    return (
      <div>
        {data.length > 0 && (
          <TagCloud minSize={12} maxSize={35} tags={mydata} />
        )}
      </div>
    );
  };

  return <SimpleCloud mydata={data} />;
};
