import styles from './styles.module.css';
import React, { useEffect, useState, useRef } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  getMessageList,
  liveMessageUpdateAsync,
  getMessageListAsync,
} from 'features/messagesSlice';
import { io } from 'socket.io-client';
import { current } from 'immer';
import { store } from 'store';
import { TagCloud } from 'react-tagcloud';

export const WordCloud = () => {
  let currentMessages = useSelector(state => state.messages.messageList);

  const hash = {};
  let data = [];
  useEffect(() => {
    setTimeout(() => {
      for (let i = 0; i < currentMessages.length; i++) {
        currentMessages[i].message.split(' ').forEach(el => {
          hash[el] = (hash[el] || 0) + 1;
        });
      }
      let counter = 0;
      for (const prop in hash) {
        if (hash[prop] > 10) {
          data.push({
            value: prop,
            count: hash[prop],
            key: String(counter),
          });
          counter++;
        }
      }
      console.log(data);
    }, 1000);
  });
  console.log('TAGCLOUD', hash, data, currentMessages);

  return (
    <div>
      {data.length > 0 ? (
        <TagCloud minSize={12} maxSize={35} tags={data} />
      ) : (
        <></>
      )}
    </div>
  );
};
