import React, { useEffect, useState } from 'react';

import { useSelector } from 'react-redux';

import { TagCloud } from 'react-tagcloud';

export const WordCloud = () => {
  let currentMessages = useSelector(state => state.messages.messageList);

  const [data, setData] = useState([]);

  const hash = {};

  useEffect(() => {
    for (let i = 0; i < currentMessages.length; i++) {
      currentMessages[i].message.split(' ').forEach(el => {
        hash[el] = (hash[el] || 0) + 1;
      });
    }
    let counter = 0;
    for (const prop in hash) {
      if (hash[prop] > 25) {
        setData({
          value: prop,
          count: hash[prop],
          key: String(counter),
        });
        counter++;
      }
    }
    console.log('setstate data', data);
  }, []);

  const SimpleCloud = data => {
    if (data.length > 0) {
      return (
        <div>
          <TagCloud minSize={12} maxSize={35} tags={data} />
        </div>
      );
    } else {
      return (
        <div>
          <h1> Loading Wordcloud </h1>
        </div>
      );
    }
  };

  return (
    <div>
      {' '}
      <SimpleCloud data={data} />
    </div>
  );
};
